class WebcastParserTests: XCTestCase {
    
    func testPaserMethod() {
        let expectedWebcasts = getExpectedWebcasts()
        let webcasts = WebcastParser().parseHTML(html: htmlString)
        XCTAssertEqual(expectedWebcasts, webcasts)
    }
    
    func getExpectedWebcasts() -> [Webcast] {
