        let w1 = Webcast()
        w1.title = "Conversation with Pirelli"
        w1.date = "March 22, 2018"
        w1.imageLink = "/assets/img/webcasts/server.svg"
        w1.description = "Join Bas Peters, Solutions Engineer at GitHub for a 30-minute webcast to discuss data science and analytics within Pirelli, and how they use GitHub to support their workflow."
