        w2.imageLink = "/assets/img/webcasts/Octicons-mark-github.svg"
        w2.description = "In this third webinar in the Data Science Series, we have a conversation with the GitHub data science team to learn more about what they are working on."
        w2.url = "/webcasts/How-GitHub-is-using-data-science/"
        expectedWebcasts.append(w2)
        return expectedWebcasts
    }
   
